        convoList: '',
        foundContact: '',
        selectedConvo: ''
    })
    useEffect(()=>{
        axios.get('/api/convo', {withCredentials : true})
        .then(res => {
            setUserData({convoList: res.data.data.convoList})
        })
        .catch((err) =>{
            // console.log(err)
        })
    }, [])

