import { useEffect, useState } from "react"
import axios from  'axios';
import FlashError from "../flashError";
import {io} from 'socket.io-client'

export default function Messages({id, newMessage}){
    console.log(id)
    const [error, setError] = useState({status: 200, message: ''});
    const [messages, setMessages] = useState([])
    const controller = new AbortController();
    const socket = io();

    useEffect(()=>{
        socket.on("connect", () => {
            console.log(socket.id); // x8WIv7-mJelg7on_ALbx
          });
        socket.emit("create-room", id);
    }, [])

    useEffect(() => {
        // fetch messages
        axios.get(`/api/convo/${id}/message/n`, {withCredentials : true})
        .then(res => {
            setMessages([...res.data])
            setError({status: res.status, message: res.statusText})
        })
        .catch(err =>{
            if (axios.isCancel(err)) {
                console.log('successfully aborted');
            } else {
                console.log(err)
                setError({status:  err.response.status, message: err.response.statusText})
            }
        })
        // cancel the request before component unmounts
        return () => {
            console.log('aborted')
            controller.abort()
        };
    }, [id, newMessage])
   
    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return (
        <>
          {messages.map((m, i) =>      
            <div key={i}>
                <h4> Sender {m.sender} Receiver {m.receiver} </h4>
                <p> {m.body} - {m.creationTime}</p> 
            </div>
        )}
        </>

    )
    
}
