import React, { useState, useEffect } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";
import FlashError from "../flashError";
import Messages from "./messages";
import SearchContact from "./searchContact";
const controller = new AbortController();
//TODO:
//1. add 'add button'
//2. add logout
//3. avoid going to login if signed in
export default function Main() {
  const [selectedConvo, setSelectedConvo] = useState(null);
  const [error, setError] = useState({ status: 200, message: "" });
  const [deleteConvo, setDeleteConvo] = useState("");
  const [userData, setUserData] = useState({
    currentUser: {},
    convoList: [],
  });

  useEffect(() => {
    // load user data
    axios
      .get("/api/convo", { withCredentials: true })
      .then((res) => {
        setUserData({
          currentUser: res.data.contactInfo,
          convoList: res.data.convoList,
        });
      })
      .catch((err) => {
        setError({ status: err.response.status, message: err.message });
        console.log(err);
      });

    return () => {
      console.log("aborted");
      controller.abort();
    };
  }, []);

  function handleDeleteConvo(e) {
    axios
      .post(`/api/convo/${deleteConvo}?_method=DELETE`, {
        withCredentials: true,
      })
      .then((res) => {
        console.log(res);
        setError({ status: res.status, message: res.statusText });
      })
      .catch((err) => {
        setError({ status: err.response, message: "Server Error" });
      });
  }

  function handleLogout() {
    // log out the user from backend
    axios
      .get("/api/logout", { withCredentials: true })
      .then((res) => {
        console.log(res);
        setError({ status: res.status, message: res.statusText });
      })
      .catch((err) => {
        setError({ status: err.response, message: err.response });
      });
    // set the is loggedIn to false so that we can redirect to /login
    localStorage.setItem("isLoggedIn", false);
  }

  if (error.status !== 200) {
    return <FlashError status={error.status} message={error.message} />;
  }

  return (
    <>
      <div className="md:container h-screen overflow-hidden py-20 flex relative">
        <a
          onClick={handleLogout}
          className="absolute top-0 right-0 mt-7 z-20"
          href="/login"
        >
          <img className="w-9 h-9 " src="/logoutIcon.svg" alt="sign out"></img>
        </a>
        <div className="w-1/4 h-full p-8 mr-8 rounded-3xl bg-light-grey drop-shadow-lg">
          {<SearchContact />}
          <ul className="mt-8 space-y-5">
            {userData.convoList.map((convo, i) => (
              <li
                className="list-none relative space-y-4 flex items-center justify-center"
                key={i}
              >
                <button
                  className="w-full text-xl text-right font-medium text-slate-100"
                  onClick={() => setSelectedConvo(convo._id)}
                >
                  {userData.currentUser._id === convo.sender._id
                    ? convo.receiver.username
                    : convo.sender.username}
                </button>
                <form
                  className="inline-block absolute right-0 "
                  onSubmit={handleDeleteConvo}
                >
                  <button
                    value={deleteConvo}
                    onClick={() => {
                      setDeleteConvo(convo._id);
                    }}
                  >
                    <img
                      className="h-5 w-5"
                      src="/deleteIcon.svg"
                      alt="delete button"
                    />
                  </button>
                </form>
              </li>
            ))}
          </ul>
        </div>

        <div className="w-3/4 h-full relative">
          {selectedConvo && (
            <Messages
              id={selectedConvo}
              currentUser={userData.currentUser.username}
            />
          )}
        </div>
      </div>
    </>
  );
}
