import React, {useState, useEffect} from "react";
import { Navigate, useParams } from "react-router-dom";
import axios from  'axios';
import FlashError from "../flashError";
import ConvoList from "./convoList";
import Messages from "./messages";
import {io} from 'socket.io-client'
const socket = io(`http://localhost:3000`);

function Main(){
    const {id} = useParams();
    const [convoID, setConvoID] = useState(id)
    const [error, setError] = useState({status: 200, message: ''});
    const [message, setMessage] = useState(null);

    function handleSubmit(e){
        e.preventDefault();
        // socket.emit('newMessage', message)

        axios.post(`/api/convo/${id}/message/n`, {'message': e.target[0].value})
        .then( res => {
            socket.on('newMessage', (m) => {console.log('m', m)})
            setMessage(e.target[0].value);
        })
        .catch(err => {
            setError({status: err.response.status, message: err.response.statusText})
        });
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return(
        <div>
            <h1>Hello this is MessageHub</h1>
            <ul>
                {<ConvoList />}
            </ul>

            {id &&
            <div>
                {<Messages id={convoID}/>}
            </div>
            }

            {id &&
            <form onSubmit={handleSubmit}>
                <textarea type="text" placeholder="Your Message Here"></textarea>
                <br/>
                <button type="submit"> Send</button>
            </form> }

        </div>
    )
}

export default Main;
