import { useEffect, useState } from "react"
import axios from  'axios';
import FlashError from "../flashError";
import {io} from 'socket.io-client'
const socket = io('http://localhost:3000/');
const controller = new AbortController();

export default function Messages({id, currentUser}){
    const [error, setError] = useState({status: 200, message: ''});
    const [messages, setMessages] = useState([])
    const [newMessage, setNewMessage] = useState([])

    useEffect(() => {
        // fetch messages
        axios.get(`/api/convo/${id}/message/n`, {withCredentials : true})
        .then(res => {
            setMessages([...res.data])
            setError({status: res.status, message: res.statusText})
        })
        .catch(err =>{
                setError({status:  err.response.status, message: err.response.statusText})
        })

        socket.on("connect", () => {
            console.log('connected')
            socket.emit("join-room", id);
        });
        socket.on('newMessage', (m) => {
            console.log('Socket Listener: ', m)
            setMessages(prevMessages => [...prevMessages, m.data]
            )})
        // cancel the request before component unmounts
        return () => {
            console.log('aborted')
            controller.abort()
        };
    }, [id])

    function handleSendMessage(e){
        e.preventDefault();
        axios.post(`/api/convo/${id}/message/n`, {'message': newMessage})
        .then( res => {
            socket.emit('sendMessage',  {res, id});
            e.target[0].value ='';
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response.status, message: err.response.statusText});
        });
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return (
        <>
            {/* determine the color for the sender and receiver */}
            {messages.map((m, i) => 
                <div key={i} className="flex flex-col">
                    <p  className={currentUser === m.sender?
                    'text-gray-900 w-fit max-w-sm self-end bg-sender p-5 mb-2 text-lg rounded-lg break-words relative': 
                    'text-gray-900 w-fit max-w-sm bg-receiver p-5 mb-2 text-lg rounded-lg break-words relative'}>
                        {m.body} 
                    </p> 
                    <span className={currentUser === m.sender? 
                    'text-gray-600 text-xs self-end mb-7': 
                    'text-gray-600 text-xs mb-7'}>
                        {m.creationTime}
                    </span>
                </div>
            )}
            <form className="w-full text-slate-100 sticky bottom-0 right-0 whitespace-nowrap" onSubmit={handleSendMessage}>
                <input type="text" id="message" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} className="w-3/4 p-3 overflow-hidden bg-transparent border rounded-lg" placeholder="Write your message here" required></input>
                <button className="p-3">Send</button>
            </form> 
        </>

    )
    
}
