import React, {useState, useEffect} from "react";
import axios from  'axios';
// import ValidationError from "../flashError";

function Convo(){
    const [userData, setUserData] = useState({
        convoList: '',
        foundContact: '',
        selectedConvo: ''
    })

    useEffect(()=>{
        axios.get('/api/convo', {withCredentials : true})
        .then(res => {
            const resData = res.data.data;
            setUserData({
                convoList: resData.convoList,
                foundContact: resData.foundContact,
                selectedConvo: resData.selectedConvoID
            })
        })
        .catch((err) =>{
            console.log(err)
        })
    }, [])

