import axios from 'axios';
import React, {useState,useEffect} from 'react';
import FlashError from "../flashError";
const controller = new AbortController();

export default function SearchContact(){
    const [result, setResult] = useState();
    const [username, setUsername] = useState('');
    const [error, setError] = useState({status: 200, message: ''});
    const [addUser, setAddUser] = useState('');

    // useEffect(() => {
    //     if (searchedUsername){
    //         axios.get(`/api/convo?username=${searchedUsername}`, {withCredentials : true})
    //         .then( res => {
    //             setResult(res.data);
    //             setError({status: res.status, message: res.statusText});
    //         })
    //         .catch(err => {
    //             setError({status: err.response, message: err.response});
    //         });
    //         // cancel the request before component unmounts
    //         return () => {
    //             console.log('aborted')
    //             controller.abort()
    //         };
    //     }
    // }, [searchedUsername])

    // useEffect(() => {
    //     if (addUser){
    //         axios.get(`/api/convo?username=${searchedUsername}`, {withCredentials : true})
    //         .then( res => {
    //             setResult(res.data);
    //             setError({status: res.status, message: res.statusText});
    //         })
    //         .catch(err => {
    //             setError({status: err.response, message: err.response});
    //         });
    //         // cancel the request before component unmounts
    //         return () => {
    //             console.log('aborted')
    //             controller.abort()
    //         };
    //     }
    // }, [searchedUsername])
    
    // function handleSearchUser(e){
    //     e.preventDefault();
    //     setSearchedUsername(username)
    //     setUsername(username)
    // }

    function handleSearchUser(e){
        e.preventDefault();
        axios.get(`/api/convo?username=${username}`, {withCredentials : true})
        .then( res => {
            setResult(res.data);
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response, message: err.response});
        });
        setUsername(username)
    }

    function handleAddUser(e){
        e.preventDefault();
        axios.post(`/api/convo`, {withCredentials : true, body:''})
        .then( res => {
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response, message: err.response});
        });
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};

    return (
         <>
            <form onSubmit={handleSearchUser}>
                <label htmlFor='username'>Find User</label>
                <input name="username" id='username' value={username} onChange={(e) => setUsername(e.target.value)} type="text" placeholder='Username'/>
                <button>Find</button>
            </form>
            {result  && result.length>0 &&
            <form onSubmit={handleAddUser}>
                <h3>{result[0].username}</h3>
                <input type="hidden" value='' />
                <button>Add Contact</button>
            </form>
            }
            {result && result.length===0 && <h3>No User Found</h3>}
         </>
    )
}
