import React, {useState} from 'react';
import axios from 'axios';
import {Navigate} from 'react-router-dom'
import FlashError from '../flashError'

function Register(){
    const [error, setError] = useState({status: '', message: ''});

    async function handleSubmit(e) {
        e.preventDefault();
        const form = e.target;

        axios.post('/api/register', { username: form[0].value, password: form[1].value})
            .then((res) => {
                setError({status: res.status, message: res.data.message})
            })
            .catch((error) => {
                setError({status:  error.response.status, message: error.response.data.message})
        });
        //  GET SUCCESS MESSAGE: res.data.message 
        //  GET ERROR MESSAGE: error.response.data.message

    }
    return (
        <div className='md:container h-screen flex justify-center items-center'>
            <div className="w-1/3 h-2/3 p-15">
                <h1 className="">Sign up to MessageHub</h1>
                <form onSubmit={handleSubmit} className='block'>
                    <label className='block'>
                        <span className='block'>Username</span>
                        <input name="username" id='username' type="text" placeholder='Username'/>
                    </label>
                    <label className='block'>
                        <span className='block'>Password</span>
                        <input name="password" id='password' type="password" placeholder='Password'/>
                    </label>
                    <button>Sign Up</button> 
                </form>
                {error.status === 200 ? <Navigate to="/login"/>: <FlashError status={error.status} message={error.message} />}
            </div>
        </div>
    );
}

export default Register;
