import React, {useState, useEffect, useLayoutEffect} from "react";
import axios from  'axios';
import ValidationError from "../flashError";
import ShowConvoList from "./convoList";
function Convo(){
    const [userData, setUserData] = useState({
        convoList: [],
        foundContact: '',
        selectedConvo: ''
    })

    useLayoutEffect(()=>{
        async function getUserData(){
            await axios.get('/api/convo', {withCredentials : true})
            .then(res => {
                setUserData({
                    [convoList]: res.data.convoList,
                    foundContact: res.data.foundContact,
                    selectedConvo: res.data.selectedConvoID
                })
    
            })
            .catch((err) =>{
                console.log(err)
            })
        }
        getUserData();
    }, [])

    return(
        <div>
            <h1>Hello this is MessageHub</h1>
            <ul>
                {userData.convoList.map((e) => <li key={e._id}>{e.sender.username}</li>)};
            </ul>
            {console.log(userData.convoList)}
            <ShowConvoList {...userData.convoList}/>
        </div>
    )
}

export default Convo;
