import { useEffect, useState } from "react"
import axios from  'axios';
import FlashError from "../flashError";
import {io} from 'socket.io-client'
const socket = io('http://localhost:3000/');
const controller = new AbortController();

export default function Messages({id, currentUser}){
    const [error, setError] = useState({status: 200, message: ''});
    const [messages, setMessages] = useState([])
    const [newMessage, setNewMessage] = useState([])

    useEffect(() => {
        // fetch messages
        axios.get(`/api/convo/${id}/message/n`, {withCredentials : true})
        .then(res => {
            setMessages([...res.data])
            setError({status: res.status, message: res.statusText})
        })
        .catch(err =>{
                setError({status:  err.response.status, message: err.response.statusText})
        })

        socket.on("connect", () => {
            console.log('connected')
            socket.emit("join-room", id);
        });
        socket.on('newMessage', (m) => {
            console.log('Socket Listener: ', m)
            setMessages(prevMessages => [...prevMessages, m.data]
            )})
        // cancel the request before component unmounts
        return () => {
            console.log('aborted')
            controller.abort()
        };
    }, [id])

    function handleSendMessage(e){
        e.preventDefault();
        axios.post(`/api/convo/${id}/message/n`, {'message': newMessage})
        .then( res => {
            socket.emit('sendMessage',  {res, id});
            e.target[0].value ='';
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response.status, message: err.response.statusText});
        });
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return (
        <>
            {/* determine the color for the sender and receiver */}
            {messages.map((m, i) =>      
                <p key={i} className={currentUser === m.sender?
                'text-gray-900 max-w-fit bg-sender p-5 mb-6 text-lg rounded-lg absolute right-0': 
                'text-slate-100 max-w-fit bg-receiver p-5 mb-6 text-lg rounded-lg'}>
                Sender {m.sender}: {m.body} - {m.creationTime}
                </p> 
            )}
            <form className="bottom-0 sticky" onSubmit={handleSendMessage}>
                <textarea type="text" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} placeholder="Write your message here" required></textarea>
                <button type="submit"> Send</button>
            </form> 
        </>

    )
    
}
