import React, {useState, useEffect} from "react";
import axios from  'axios';
import ValidationError from "../flashError";
import ShowConvoList from "./convoList";
function Convo(){
    const [userData, setUserData] = useState({
        convoList: [],
        foundContact: '',
        selectedConvo: ''
    })

    useEffect(()=>{
        axios.get('/api/convo', {withCredentials : true})
        .then(res => {
            setUserData({
                convoList: res.data.convoList,
                foundContact: res.data.foundContact,
                selectedConvo: res.data.selectedConvoID
            })
        })
        .catch((err) =>{
            console.log(err)
        })
    }, [])
    const convoList = userData.convoList.map((e) => {return e});
    console.log(convoList)
    

    return(
        <div>
            <h1>Hello this is MessageHub</h1>
            {/* <ShowConvoList convos={userData.convoList}/> */}
        </div>
    )
}

export default Convo;
