import React, {useState} from 'react';
import axios from 'axios';
import {Navigate} from 'react-router-dom'
import ValidationError from '../validation'



function Login(){
    const [errorMessage, setErrorMessage] = useState('')

    async function handleSubmit(e) {
        e.preventDefault();
        const form = e.target;

        axios.post('/register', { username: form[0].value, password: form[1].value})
            .then((res) => {
            setErrorMessage(res.status)
            })
            .catch((error) => {
            setErrorMessage(error.response.data.message)
        });
        //  GET SUCCESS MESSAGE: res.data.message 
        //  GET ERROR MESSAGE: error.response.data.message

    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
            <label htmlFor='username'>Username</label>
            <br />
            <input name="username" id='username' type="text" placeholder='Username'/>
            <br />
            <label htmlFor='password'>Password</label>
            <br/>
            <input name="password" id='password' type="password" placeholder='Password'/>
            <br />
            <input type="submit" value="Submit" />
        </form>
            {errorMessage === "Success" ? <Navigate to="/login"/>: <ValidationError message={errorMessage} />}
        </div>
      );
}

export default Login;
