import { Navigate } from "react-router-dom";

function FlashError({ status, message }) {
  // 401 - access not authorized
  if (status === 401) {
    localStorage.setItem("isLoggedIn", false);
  }

  return (
    <div>
      <h4>
        {status} {message}
      </h4>
      {status === 401 && <a href="/login">Go to Login</a>}
    </div>
  );
}
export default FlashError;
