import React, {useState, useEffect, useLayoutEffect} from "react";
import axios from  'axios';
import ValidationError from "../flashError";
import ShowConvoList from "./convoList";
function Convo(){
    const [userData, setUserData] = useState({
        convoList: [],
        foundContact: '',
        selectedConvo: ''
    })

    useLayoutEffect(()=>{
        axios.get('/api/convo', {withCredentials : true})
        .then(res => {
            setUserData({
                convoList: res.data.convoList,
                foundContact: res.data.foundContact,
                selectedConvo: res.data.selectedConvoID
            })

        })
        .catch((err) =>{
            console.log(err)
        })
    }, [])

    return(
        <div>
            <h1>Hello this is MessageHub</h1>
            <ul>
                {userData.convoList.map((e) => <li>{e.sender.username}</li>)};
            </ul>
            {console.log(userData.convoList)}
            <ShowConvoList convos={'hi'}/>
        </div>
    )
}

export default Convo;
