import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Navigate} from 'react-router-dom'
import FlashError from '../flashError'

function Login(){
    const [error, setError] = useState({status: '', message: ''});
    console.log(localStorage.getItem('isLoggedIn'))
    if (localStorage.getItem('isLoggedIn')) {<Navigate to="/convo"/>};

    function handleSubmit(e) {
        e.preventDefault();
        const form = e.target;

        axios.post('/api/login', {username: form[0].value, password: form[1].value}, { withCredentials: true })
            .then((res) => {
                localStorage.setItem('LoggedIn', true);
                setError({status: res.status, message: res.data.message})
            })
            .catch((error) => {
                setError({status:  error.response.status, message: 'Error: Incorrect username or password'})
                console.log( error)
        });
    }

    // useEffect(() => {
    //     const loggedInUser = localStorage.getItem("user");
    //     if (loggedInUser) {
    //       const foundUser = JSON.parse(loggedInUser);
    //       console.log(foundUser)
    //     //   setUser(foundUser);
    //     }
    //   }, []);

    return (
        <div>
            <form onSubmit={handleSubmit}>
            <label htmlFor='username'>Username</label>
            <br />
            <input name="username" id='username' type="text" placeholder='Username'/>
            <br />
            <label htmlFor='password'>Password</label>
            <br/>
            <input name="password" id='password' type="password" placeholder='Password'/>
            <br />
            <input type="submit" value="Submit" />
        </form>
            {error.status === 200 ? <Navigate to="/convo"/>: <FlashError status={error.status} message={error.message} />}
        </div>
      );
}

export default Login;
