import React, {useState, useEffect, useLayoutEffect} from "react";
import { Navigate, useParams } from "react-router-dom";
import axios from  'axios';
import FlashError from "../flashError";
import ShowConvos from "./convoList";
import Messages from "./message";
import io from 'socket.io-client'

function Convo(){
    const socket = io();
    const {id} = useParams();
    const [error, setError] = useState({status: '', message: ''});
    const [userData, setUserData] = useState({
        currentUser: {},
        convoList: [],
        foundContact: undefined,
    })
    const [messages, setMessages] = useState()

    useEffect(()=>{
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        // console.log(source)
        if(!id){
            axios.get('/api/convo', {withCredentials : true, cancelToken: source.token})
            .then(res => {
                setUserData({
                    currentUser: res.data.contactInfo,
                    convoList: res.data.convoList,
                    foundContact: res.data.foundContact,
                })
                setError({status: res.status, message: res.statusText})
                console.log(res)
            })
            .catch((err) =>{
                if (axios.isCancel(err)) {
                    console.log('successfully aborted');
                  } else {
                    console.log(err)
                    setError({status:  err.response.status, message: err.response.statusText})
                  }
            })
        }
   
        if (id){
            axios.get(`/api/convo/${id}/message/n`, {withCredentials : true, cancelToken: source.token})
            .then(res => {
                console.log(res.data)
                setMessages(res)
                setError({status: res.status, message: res.statusText})
            })
            .catch((err) =>{
                if (axios.isCancel(err)) {
                    console.log('successfully aborted');
                  } else {
                    console.log(err)
                    setError({status:  err.response.status, message: err.response.statusText})
                  }
            })
        }
        // cancel the request before component unmounts
        return () => {
            source.cancel();
        };
    }, [id])

    // function getMessages() {
    //     $.get(`http://localhost:3000/convo/${id}/message/n`, (data) => {
    //         data.forEach(addMessages);
    //     })
    // }

    // socket.on('newMessage', addMessages)

    // function addMessages(message) {
    //     $("#messages").append(`<h4> Sender ${message.sender} </h4> <h4> Receiver ${message.receiver} </h4><p> ${message.body} - ${message.creationTime}</p>`)
    // }
    // useEffect(()=>{
    //     const CancelToken = axios.CancelToken;
    //     const source = CancelToken.source();
    //     // console.log(source)
    //     axios.get(`/api/convo/${userData.selectedConvo}/message/n`, {withCredentials : true, cancelToken: source.token})
    //     .then(res => {
    //         console.log(res)
    //         setMessages([...messages, res])
    //         setError({status: res.status, message: res.statusText})
    //     })
    //     .catch((err) =>{
    //         if (axios.isCancel(err)) {
    //             console.log('successfully aborted');
    //           } else {
    //             console.log(err)
    //             setError({status:  err.response.status, message: err.response.statusText})
    //           }
    //     })
    //     // cancel the request before component unmounts
    //     return () => {
    //         source.cancel();
    //       };
    // }, [userData.selectedConvo])


    if (error.status !== 200){
        return(
            <FlashError status={error.status} message={error.message}/>
        )
    } 

    return(
        <div>
            <h1>Hello this is MessageHub</h1>
            <ul>
                {userData.convoList.map((e) => <li key={e._id}> <ShowConvos user={userData.currentUser} convo={e}/></li>)}
            </ul>
            {id && messages.map(m => <Messages {...m}/>)}
        </div>
    )
}

export default Convo;
