import React, {useState} from 'react';
import axios from 'axios';
import {Navigate} from 'react-router-dom'
import FlashError from '../flashError'

function Register(){
    const [error, setError] = useState({status: '', message: ''});

    async function handleSubmit(e) {
        e.preventDefault();
        const form = e.target;

        axios.post('/api/register', { username: form[0].value, password: form[1].value})
            .then((res) => {
                setError({status: res.status, message: res.data.message})
            })
            .catch((error) => {
                setError({status:  error.response.status, message: error.response.data.message})
        });
        //  GET SUCCESS MESSAGE: res.data.message 
        //  GET ERROR MESSAGE: error.response.data.message

    }
    return (
        <div className='md:container h-screen flex justify-center items-center'>
            <div className="w-1/3 h-1/2 p-12 space-y-8">
                <h1 className="text-3xl text-center">Sign up to MessageHub</h1>
                <form onSubmit={handleSubmit} className='block space-y-8'>
                    <label className='block space-y-2'>
                        <span className='block text-lg'>Username</span>
                        <input className='w-full p-2.5' name="username" id='username' type="text" placeholder='Enter your username'/>
                    </label>
                    <label className='block space-y-2'>
                        <span className='block text-lg'>Password</span>
                        <input className='w-full p-2' name="password" id='password' type="password" placeholder='Enter your password'/>
                    </label>
                    <button className='w-full'>Sign Up</button> 
                </form>
            </div>
            {error.status === 200 ? <Navigate to="/login"/>: <FlashError status={error.status} message={error.message} />}
        </div>
    );
}

export default Register;
