import axios from 'axios';
import React, {useState,useEffect} from 'react';
import FlashError from "../flashError";

export default function SearchContact({username}){
    console.log(username)
    const [searchedUsername, setSearchedUsername] = useState(username);
    const [error, setError] = useState({status: 200, message: ''});

    useEffect(() => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        axios.get(`/api/convo?username=${searchedUsername}`, {withCredentials : true,  cancelToken: source.token})
        .then( res => {
            console.log('sContact',res)
            // setSearchedResult(res);
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response.status, message: err.response.statusText});
        });
        // cancel the request before component unmounts
        return () => {
            source.cancel();
        };
    }, [searchedUsername])

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};

    return (
         <>
            
         </>
    )
}
