import { Navigate } from "react-router-dom";

function FlashError({status, message}) {
    if (status === 401){
        localStorage.setItem('isLoggedIn', false)
        return (
            // className="my-5 transition-opacity-100 text-center text-xl text-red-500 p-2 font-extrabold"
            <div>
                <h4>
                    {status} {message}
                    <br/>
                    <a href="/login">Go to Login</a>
                </h4>
            </div>
        )
    }
    
    return (
        // className="my-5 transition-opacity-100 text-center text-xl text-red-500 p-2 font-extrabold"
        <div>
            <h4>
                {status} {message}
            </h4>
        </div>
    )
}
export default FlashError;
