import React, {useState, useEffect, useLayoutEffect} from "react";
import { Navigate } from "react-router-dom";
import axios from  'axios';
import FlashError from "../flashError";
import ShowConvos from "./convoList";
import Messages from "./message";
import io from 'socket.io-client'

function Convo(){
    const socket = io();
    const [error, setError] = useState({status: '', message: ''});
    const [userData, setUserData] = useState({
        currentUser: {},
        convoList: [],
        foundContact: undefined,
        selectedConvo: undefined
    })

    useEffect(()=>{
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        // console.log(source)
        axios.get('/api/convo', {withCredentials : true, cancelToken: source.token})
        .then(res => {
            setUserData({
                currentUser: res.data.contactInfo,
                convoList: res.data.convoList,
                foundContact: res.data.foundContact,
                selectedConvo: res.data.selectedConvoID
            })
            setError({status: res.status, message: res.statusText})
        })
        .catch((err) =>{
            if (axios.isCancel(err)) {
                console.log('successfully aborted');
              } else {
                console.log(err)
                setError({status:  err.response.status, message: err.response.statusText})
              }
        })
        // cancel the request before component unmounts
        return () => {
            source.cancel();
          };
    }, [])

    useEffect(()=>{
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        // console.log(source)
        axios.get(`/api/convo/${userData}`, {withCredentials : true, cancelToken: source.token})
        .then(res => {
            console.log(res)
            setError({status: res.status, message: res.statusText})
        })
        .catch((err) =>{
            if (axios.isCancel(err)) {
                console.log('successfully aborted');
              } else {
                console.log(err)
                setError({status:  err.response.status, message: err.response.statusText})
              }
        })
        // cancel the request before component unmounts
        return () => {
            source.cancel();
          };
    }, [userData.selectedConvo])

    const [isConnected, setIsConnected] = useState(socket.connected);
    const [lastPong, setLastPong] = useState(null);
  
    useEffect(() => {
      socket.on('connect', () => {
        setIsConnected(true);
      });
  
      socket.on('disconnect', () => {
        setIsConnected(false);
      });
  
      socket.on('pong', () => {
        setLastPong(new Date().toISOString());
      });
  
      return () => {
        socket.off('connect');
        socket.off('disconnect');
        socket.off('pong');
      };
    }, []);
    const sendPing = () => {
        socket.emit('ping');
      }
    
    return(
        <div>
            <h1>Hello this is MessageHub</h1>
            <ul>
                {userData.convoList.map((e) =>  <li key={e._id}><ShowConvos user={userData.currentUser} convo={e}/></li>)}
            </ul>
            {userData.selectedConvo && <Messages {...userData.selectedConvo}/>}
            {error.status !== 200 && <FlashError status={error.status} message={error.message}/>}
            <div>
                <p>Connected: { '' + isConnected }</p>
                <p>Last pong: { lastPong || '-' }</p>
                <button onClick={ sendPing }>Send ping</button>
            </div>
        </div>
    )
}

export default Convo;
