import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Navigate} from 'react-router-dom'
import FlashError from '../flashError'

function Login(){
    const [error, setError] = useState({status: '', message: ''});

    function handleSubmit(e) {
        e.preventDefault();
        const form = e.target;
        axios.post('/api/login', {username: form[0].value, password: form[1].value}, { withCredentials: true })
            .then((res) => {
                localStorage.setItem('isLoggedIn', true);
                setError({status: res.status, message: res.data.message})
            })
            .catch((error) => {
                setError({status:  error.response.status, message: 'Error: Incorrect username or password'})
                console.log( error)
        });
    }
    console.log(localStorage.getItem('isLoggedIn'))
   console.log(typeof JSON.parse(localStorage.getItem('isLoggedIn')))
    // if already logged in redirect to main page   
    useEffect(() => {
        if (localStorage.getItem('isLoggedIn') && JSON.parse(localStorage.getItem('isLoggedIn')) === true) {
            console.log('set status'); setError({status: 200, message: ''})
        } else{
            setError({status: '', message: ''})
        }
    }, [])
    
    return (
        <div className='md:container h-screen flex justify-center items-center space-x-20'>
            <img className="max-w-3xl min-w-sm" src="/hero.svg" alt="hero image"/>
            <div className="w-1/3 h-1/2 p-16 flex flex-col justify-center rounded-lg bg-bluish-white drop-shadow-lg">
                {/* <h1 className="text-3xl text-center font-bold mb-10">Sign up to MessageHub</h1> */}
                <form onSubmit={handleSubmit} className='block'>
                    <label className='block space-y-1 mb-5'>
                        <span className='block text-lg'>Username</span>
                        <input className='w-full p-3 rounded-lg border border-slate-300' name="username" id='username' type="text" placeholder='Enter your username' required/>
                    </label>
                    <label className='block space-y-1 mb-8'>
                        <span className='block text-lg'>Password</span>
                        <input className='w-full p-3 rounded-lg border border-slate-300' name="password" id='password' type="password" placeholder='Enter your password' required/>
                    </label>
                    <button className='w-full p-4 text-2xl rounded-lg bg-button-color text-slate-100 '>Sign Up</button> 
                </form>
                <div className='text-center mt-3'><p>New to MessageHub? <a href='/register' className='font-bold'>Sign up now</a></p> </div>
            </div>
            {/* {error.status === 200 ? <Navigate to="/convo"/>: <FlashError status={error.status} message={error.message} />} */}
        </div>
      );
}

export default Login;
