import { useEffect, useState } from "react"
import axios from  'axios';
import FlashError from "../flashError";

export default function Messages({id}){
    console.log(id)
    const [error, setError] = useState({status: 200, message: ''});
    const [messages, setMessages] = useState([])

     // if convo selected, then load messages
    useEffect(() => {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();
        // fetch messages
        axios.get(`/api/convo/${id}/message/n`, {withCredentials : true, cancelToken: source.token})
        .then(res => {
            setMessages([...res.data])
            setError({status: res.status, message: res.statusText})
        })
        .catch(err =>{
            if (axios.isCancel(err)) {
                console.log('successfully aborted');
            } else {
                console.log(err)
                setError({status:  err.response.status, message: err.response.statusText})
            }
        })
        // cancel the request before component unmounts
        return () => {
            source.cancel();
        };
    }, [id])
   
    function displayMessages() {
        messages.map((m, i) =>      
            <div key={i}>
                <h4> Sender {m.sender} Receiver {m.receiver} </h4>
                <p> {m.body} - {m.creationTime}</p> 
            </div>
        )
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return (
        <>
          {displayMessages()}
        </>

    )
    
}
