import axios from 'axios';
import React, {useState,useEffect} from 'react';
import FlashError from "../flashError";
const controller = new AbortController();

export default function SearchContact(){
    const [searchedResult, setSearchedResult] = useState();
    const [searchedUsername, setSearchedUsername] = useState(null);
    const [error, setError] = useState({status: 200, message: ''});
    const [addUser, setAddUser] = useState();

    useEffect(() => {
        if (searchedUsername){
            axios.get(`/api/convo?username=${searchedUsername}`, {withCredentials : true})
            .then( res => {
                setSearchedResult(res.data);
                setError({status: res.status, message: res.statusText});
            })
            .catch(err => {
                setError({status: err.response, message: err.response});
            });
            // cancel the request before component unmounts
            return () => {
                console.log('aborted')
                controller.abort()
            };
        }
    }, [searchedUsername])
    
    function handleSearchUser(e){
        e.preventDefault();
        setSearchedUsername(e.target[0].value)
        e.target[0].value =''
    }

    function handleAddUser(e){
        e.preventDefault();
        setSearchedUsername(e.target[0].value)
        e.target[0].value =''
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};

    return (
         <>
            <form onSubmit={handleSearchUser}>
                <label htmlFor='username'>Find User</label>
                <input name="username" id='username' type="text" placeholder='Username'/>
                <button type="submit">Find</button>
            </form>
            {searchedResult  && searchedResult.length>0 &&

                <form>
                    <h3>{searchedResult[0].username}</h3>
                    setAddUser(e.target.value)/
                    <input type="hidden" name="id" value={addUser} onChange={(e) => console.log(e)}/>
                    <button>Add Contact</button>
                </form>
            }
            {searchedResult && searchedResult.length===0 && <h3>No User Found</h3>}
         </>
    )
}
