import axios from 'axios';
import React, {useState,useEffect} from 'react';
import FlashError from "../flashError";

export default function SearchContact(){
    const [username, setUsername] = useState();
    const [error, setError] = useState({status: 200, message: ''});

    // useEffect(() => {
    //     const CancelToken = axios.CancelToken;
    //     const source = CancelToken.source();
    //     axios.get(`/api/convo/`, {withCredentials : true, cancelToken: source.token})
    //     .then(res => {
    //         setError({status: res.status, message: res.statusText})
    //     })
    //     .catch(err =>{
    //         if (axios.isCancel(err)) {
    //             console.log('successfully aborted');
    //         } else {
    //             console.log(err)
    //             setError({status:  err.response.status, message: err.response.statusText})
    //         }
    //     })
    //     // cancel the request before component unmounts
    //     return () => {
    //         source.cancel();
    //     };
    // }, [])

    function handleSubmit(e){
        e.preventDefault();

        axios.get(`/api/convo?username=`, {withCredentials : true})
        .then( res => {
            setError({status: res.status, message: res.statusText})
        })
        .catch(err => {
            setError({status: err.response.status, message: err.response.statusText})
        });
    }
    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};

    return (
         <form onSubmit={handleSubmit}>
                <textarea type="text" placeholder="Your Message Here"></textarea>
                <br/>
                <button type="submit"> Send</button>
            </form> 
    )
}
