import React, {useState, useEffect, useMemo} from "react";
import { Navigate, useParams } from "react-router-dom";
import axios from  'axios';
import FlashError from "../flashError";
import ConvoList from "./convoList";
import Messages from "./messages";
import SearchContact from "./searchContact";
const controller= new AbortController();
//TODO: 
//1. add 'add button'
//2. add logout
//3. avoid going to login if signed in
export default function Main(){
    const {id} = useParams();
    const [error, setError] = useState({status: 200, message: ''});
    const [messages, setMessages] = useState([])
    const [userData, setUserData] = useState({
        currentUser: {},
        convoList: [],
    })

    const convoID = useMemo(() => {
        return id;
    }, [id]); 

    useEffect(()=>{
        // load user data
        axios.get('/api/convo', {withCredentials : true})
        .then(res => {
            setUserData({currentUser: res.data.contactInfo, convoList: res.data.convoList})
            setError({status: res.status, message: res.statusText})
        })
        .catch(err =>{
            setError({status:  err.response.status, message: err.response.statusText})
        })
    
        return () => {
            console.log('aborted')
            controller.abort()
        };
    }, []);


    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return(
        <div>
            <h1>Hello this is MessageHub</h1>
            {<SearchContact />}

            {userData.convoList.map((convo, i) => 
            <li key={i}>
                <a href={`/convo/${convo._id}`}> {userData.currentUser._id === convo.sender._id? convo.receiver.username: convo.sender.username} </a>
