import React, {useState, useEffect, useMemo, useLayoutEffect, useRef} from "react";
import { Navigate, useParams } from "react-router-dom";
import axios from  'axios';
import FlashError from "../flashError";
import Messages from "./messages";
import SearchContact from "./searchContact";
const controller= new AbortController();
//TODO: 
//1. add 'add button'
//2. add logout
//3. avoid going to login if signed in
export default function Main(){
    const {id} = useParams();
    const messagesEndRef = useRef(null)
    const [clickedUsername, setClickedUsername] = useState();
    const convoHeader = useRef(' ');
    const [error, setError] = useState({status: 200, message: ''});
    const [deleteConvoID, setDeleteConvoID] = useState('')
    const [userData, setUserData] = useState({
        currentUser: {},
        convoList: [],
    })
    const convoID = useMemo(() => {
        return id;
    }, [id]); 

    useEffect(()=>{
        // load user data
        axios.get('/api/convo', {withCredentials : true})
        .then(res => {
            setUserData({currentUser: res.data.contactInfo, convoList: res.data.convoList})
        })
        .catch(err =>{
            setError({status:  err.response.status, message: err.message})
            console.log(err)
        })
    
        return () => {
            console.log('aborted')
            controller.abort()
        };
    }, []);

    function handleDeleteConvo(e){
        axios.post(`/api/convo/${deleteConvoID}?_method=DELETE`, {withCredentials : true})
        .then( res => {
            console.log(res)
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response, message: 'Server Error'});
        });
    }

    function handleLogout(){
        // log out the user from backend
        axios.get('/api/logout', {withCredentials : true})
        .then(res => {
            console.log(res)
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response, message: err.response});
        });
        // set the is loggedIn to false so that we can redirect to /login
        localStorage.setItem('isLoggedIn', false);
    }

    const scrollToBottom = () => {
       messagesEndRef.current.scrollIntoView({ behavior: "smooth" })
    }

    useEffect(()=>{
        scrollToBottom();
    }, [userData])

 
    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return(
        <>
        <div className="md:container h-screen overflow-hidden py-20 flex relative space-x-7">
            <div className="w-1/4 h-full p-8 rounded-lg bg-light-grey drop-shadow-lg">
                <a className="hidden" href='/login'><button onClick={handleLogout}>Logout</button></a>
                {<SearchContact />}
                <ul className="mt-8 space-y-5">
                    {userData.convoList.map((convo, i) => 
                        <li className="list-none relative space-y-4 flex items-center justify-center" key={i}>
                            <a className="w-full text-xl font-medium text-slate-100" href={`/convo/${convo._id}`} >{userData.currentUser._id === convo.sender._id? convo.receiver.username: convo.sender.username}</a>
                            <form className="inline-block absolute right-0 " onSubmit={handleDeleteConvo}>
                                <button value={deleteConvoID} onClick={()=> {setDeleteConvoID(convo._id)}}><img className="h-5 w-5" src="/deleteIcon.svg" alt="delete button"/></button>
                            </form>
                        </li>
                    )}
                </ul>
            </div>
        <div className="w-full bg-gradient-to-b from-gray-800"></div>
            <div className="w-full h-full p-10 rounded-lg animate-load bg-light-grey drop-shadow-lg relative overflow-y-auto overflow-x-hidden scroll-mt-100% scroll-smooth scrollbar-thin scrollbar-thumb-rounded-lg scrollbar-thumb-button-color scrollbar-track-transparent">
                {id && <Messages id={convoID} currentUser={userData.currentUser.username}/>}
                <div ref={messagesEndRef}></div>
            </div>
        </div>

        </>
    )
}
