import React, {useState, useEffect, useMemo} from "react";
import { Navigate, useParams } from "react-router-dom";
import axios from  'axios';
import FlashError from "../flashError";
import ConvoList from "./convoList";
import Messages from "./messages";
import SearchContact from "./searchContact";
import {io} from 'socket.io-client'
// const socket = io();
const controller= new AbortController();
//TODO: 
//1. add 'add button'
//2. add logout
//3. avoid going to login if signed in
export default function Main(){
    const {id} = useParams();
    // const [convoID, setConvoID] = useState(id)
    const [error, setError] = useState({status: 200, message: ''});
    const [messages, setMessages] = useState([])
    const [searchedUsername, setSearchedUsername] = useState(null);
    const [userData, setUserData] = useState({
        currentUser: {},
        convoList: [],
    })

    const convoID = useMemo(() => {
        return id;
    }, [id]); 

    useEffect(()=>{
        // load user data
        axios.get('/api/convo', {withCredentials : true})
        .then(res => {
            setUserData({currentUser: res.data.contactInfo, convoList: res.data.convoList})
            setError({status: res.status, message: res.statusText})
        })
        .catch(err =>{
            setError({status:  err.response.status, message: err.response.statusText})
        })
    
        return () => {
            console.log('aborted')
            controller.abort()
        };
    }, []);

    // useEffect(()=>{
    //     if(convoID){
    //         // fetch messages
    //         axios.get(`/api/convo/${convoID}/message/n`, {withCredentials : true})
    //         .then(res => {
    //             setMessages([...res.data])
    //             setError({status: res.status, message: res.statusText})
    //         })
    //         .catch(err =>{
    //             setError({status:  err.response.status, message: err.response.statusText})
    //         })
    //         // cancel the request before component unmounts
    //     }
    //     return () => {
    //         console.log('aborted')
    //         controller.abort()
    //     };
    // }, [convoID])

    // useEffect(()=>{
    //     if(convoID){
    //         socket.on("connect", () => {
    //             console.log('connected')
    //             socket.emit("join-room", convoID);
    //         });
    //         socket.on('newMessage', (m) => setMessages(prevMessages => [...prevMessages, m.data]))
    //     }
    // }, [convoID])

    // function handleSendMessage(e){
    //     e.preventDefault();
    //     axios.post(`/api/convo/${id}/message/n`, {'message': e.target[0].value})
    //     .then( res => {
    //         socket.emit('sendMessage',  {res, id})
    //         setError({status: res.status, message: res.statusText})
    //     })
    //     .catch(err => {
    //         setError({status: err.response.status, message: err.response.statusText})
    //     });
    // }
   
    function findUser(e){
        e.preventDefault();
        setSearchedUsername(e.target[0].value)
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};
    
    return(
        <div>
            <h1>Hello this is MessageHub</h1>

            <form onSubmit={findUser}>
                <label htmlFor='username'>Find User</label>
                <input name="username" id='username' type="text" placeholder='Username'/>
                <button type="submit">Find</button>
            </form>

            {searchedUsername && <div>{<SearchContact username={searchedUsername} />}</div>}

            {userData.convoList.map((convo, i) => 
            <li key={i}>
                <a href={`/convo/${convo._id}`}> {userData.currentUser._id === convo.sender._id? convo.receiver.username: convo.sender.username} </a>
            </li>
            )}
            {id && <Messages id={convoID}/>}
            {/* {id && 
            <div> 
                {messages.map((m, i) =>      
                    <div key={i}>
                        <h4> Sender {m.sender}</h4>
                        <p> {m.body} - {m.creationTime}</p> 
                    </div>
                )} 
            </div>} */}

            {/* {id &&
            <form onSubmit={handleSendMessage}>
                <textarea type="text" placeholder="Your Message Here"></textarea>
                <br/>
                <button type="submit"> Send</button>
            </form> } */}

        </div>
    )
}
