import React, {useState} from "react";

export default function ShowConvos({convo, user}){
    const convoID = convo._id;
    const {_id: senderID, username: senderUsername} = convo.sender; 
    const {_id: receiverID, username: receiverUsername} = convo.receiver; 
    const currentUserID = user._id;
    console.log('curuser', currentUserID)
    console.log('sender', senderID)

    let convoName= '';
    if (currentUserID == senderID){
        convoName = receiverUsername;
    } else{
        convoName = senderUsername;
    }
    return (
        <a href={`/convo/${convoID}`}>{convoName}</a>
    )
}
