import axios from 'axios';
import React, {useState,useEffect} from 'react';
import FlashError from "../flashError";

export default function SearchContact({username}){
    const [searchedResult, setSearchedResult] = useState(username);
    const [error, setError] = useState({status: 200, message: ''});
    const controller = new AbortController();

    useEffect(() => {
        axios.get(`/api/convo?username=${username}`, {withCredentials : true})
        .then( res => {
            setSearchedResult(res.data);
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response, message: err.response});
        });
        // cancel the request before component unmounts
        return () => {
            console.log('aborted')
            controller.abort()
        };
    }, [username])

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};

    if (searchedResult.length===0) {
        return ( 
            <h3>No User Found</h3>
        )
    }

    function findUser(e){
        e.preventDefault();
        setSearchedUsername(e.target[0].value)
    }

    return (
         <>
            <form onSubmit={findUser}>
                <label htmlFor='username'>Find User</label>
                <input name="username" id='username' type="text" placeholder='Username'/>
                <button type="submit">Find</button>
            </form>
            {searchedResult && 
                <div>
                    <h3>{searchedResult[0].username}</h3>
                    <button>Add Contact</button>
                </div>
            }
         
         </>
    )
}
