import React from "react";

export default function ShowConvos({convo, user}){
    const convoID = convo._id;
    const {_id: senderID, username: senderUsername} = convo.sender; 
    const {_id: receiverID, username: receiverUsername} = convo.receiver; 
    const currentUserID = user._id;

    return (
        <a href={`/convo/${convoID}`}>{currentUserID === senderID? receiverUsername: senderUsername}</a>
    )
}
