import axios from 'axios';
import React, {useState,useEffect} from 'react';
import FlashError from "../flashError";
const controller = new AbortController();

export default function SearchContact(){
    const [result, setResult] = useState();
    const [username, setUsername] = useState('');
    const [error, setError] = useState({status: 200, message: ''});
    const [addUserID, setAddUserID] = useState('');

    // useEffect(() => {
    //     if (searchedUsername){
    //         axios.get(`/api/convo?username=${searchedUsername}`, {withCredentials : true})
    //         .then( res => {
    //             setResult(res.data);
    //             setError({status: res.status, message: res.statusText});
    //         })
    //         .catch(err => {
    //             setError({status: err.response, message: err.response});
    //         });
    //         // cancel the request before component unmounts
    //         return () => {
    //             console.log('aborted')
    //             controller.abort()
    //         };
    //     }
    // }, [searchedUsername])

    // useEffect(() => {
    //     if (addUser){
    //         axios.get(`/api/convo?username=${searchedUsername}`, {withCredentials : true})
    //         .then( res => {
    //             setResult(res.data);
    //             setError({status: res.status, message: res.statusText});
    //         })
    //         .catch(err => {
    //             setError({status: err.response, message: err.response});
    //         });
    //         // cancel the request before component unmounts
    //         return () => {
    //             console.log('aborted')
    //             controller.abort()
    //         };
    //     }
    // }, [searchedUsername])
    
    // function handleSearchUser(e){
    //     e.preventDefault();
    //     setSearchedUsername(username)
    //     setUsername(username)
    // }

    function handleSearchUser(e){
        e.preventDefault();
        axios.get(`/api/convo?username=${username}`, {withCredentials : true})
        .then( res => {
            setResult(res.data);
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response, message: err.response});
        });
        setUsername(username)
    }

    function handleAddUser(e){
        axios.post(`/api/convo`, {withCredentials : true, id: addUserID})
        .then( res => {
            setError({status: res.status, message: res.statusText});
        })
        .catch(err => {
            setError({status: err.response, message: err.response});
        });
    }

    if (error.status !== 200) {return <FlashError status={error.status} message={error.message}/>};

    return (
         <>
            <div>
                <form className='w-full whitespace-nowrap relative flex items-center z-1' onSubmit={handleSearchUser}>
                    <input className='p-2 w-full rounded-lg border border-slate-300' required id='username' value={username} onChange={(e) => setUsername(e.target.value)} type="text" placeholder='Find user'/>
                    <button className='p-3 absolute right-0'><img className="h-4 w-4" src="/searchIcon.svg"/></button>
                </form>
                <div className='py-3 w-full rounded-b-lg bg-light-grey drop-shadow-md z-0'>
                    {result  && result.length>0 &&
                    <form className='whitespace-nowrap relative flex items-center justify-center' onSubmit={handleAddUser}>
                        <h3>{result[0].username}</h3>
                        <button className='' value={addUserID} onClick={()=> setAddUserID(result[0]._id)}><img className="h-5 w-5" src="/addContact.svg"/></button>
                    </form>
                    }
                    {result && result.length===0 && <h3>No User Found</h3>}
                </div>
            </div>
         </>
    )
}
